import React ,{ Component } from "react";
import Link from "next/link";
import fetch from 'isomorphic-fetch';
import Layout from "../Components/Layout";
import { Container, Col, Form,
  FormGroup, Label, Input,
  Button } from 'react-bootstrap';
import axios from 'axios';


class AddRecord extends Component{

    constructor(props) {
        super(props);    

      this.onChangeName = this.onChangeName.bind(this);
      this.onChangeDescription = this.onChangeDescription.bind(this);
      this.onChangeIscomplete = this.onChangeIscomplete.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
          Name: '',
          description: '',
          Iscomplete:''
      }
  }
  onChangeName(e) {
    this.setState({
      Name: e.target.value
    });
  }
  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })  
  }
  onChangeIscomplete(e) {
    this.setState({
      Iscomplete: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    //console.log(`The values are ${this.state.Name}, ${this.state.description}, and ${this.state.Iscomplete}`)
 
    const obj = {}; 
    obj[ 'name' ] = this.state.Name;       
    obj[ 'description' ] = this.state.description;
    obj['isComplete'] = this.state.isComplete;
    var formData  = new FormData();
    for(var name in obj) {
    formData.append(name, obj[name]);
  }
  fetch('http://localhost:50607/api/item/createasync', {
    method: 'POST',
    body: formData
  }).then(function (response) {
    console.log(response);
  });

}
   



    render()
    {
        return(
        <Layout>
            <Form className="form">
            <FormGroup>
              <Label>Email</Label>
              <Input
                type="email"
                name="email"
                id="exampleEmail"
                placeholder="myemail@email.com"
              />
            </FormGroup>
          </Form>

              {/* <Form onSubmit={this.onSubmit}>
                  <form onSubmit={this.onSubmit}> 
                <label>
                  Name:
                 <input type="text"  value={this.state.Name} onChange={this.onChangeName} />
                </label>
                <label>
                Description:
                 <input type="text" value={this.state.description} onChange={this.onChangeDescription}  />
                </label>
                <label>
                Iscomplete:
                 <input type="checkbox"  checked={this.state.Iscomplete}
                onChange={this.onChangeIscomplete}  />
                </label>
                <input type="submit" value="Submit" />
                </form>
                </Form> */}
            <div>
               <Link href='/'>
                    <a>Return to dashboard</a>
                </Link>
            </div>
            </Layout>
          
        );
    }
}

export default AddRecord;
