import React ,{ Component } from "react";
import Link from "next/link";
import fetch from 'isomorphic-fetch';
import Layout from "../Components/Layout";
import { Table, Col, Row } from 'react-bootstrap';


class AddRecord extends Component{

    constructor(props) {
        super(props);    

      this.onChangePersonName = this.onChangePersonName.bind(this);
      this.onChangeBusinessName = this.onChangeBusinessName.bind(this);
      this.onChangeGstNumber = this.onChangeGstNumber.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
          person_name: '',
          business_name: '',
          business_gst_number:''
      }
  }
  onChangePersonName(e) {
    this.setState({
      person_name: e.target.value
    });
  }
  onChangeBusinessName(e) {
    this.setState({
      business_name: e.target.value
    })  
  }
  onChangeGstNumber(e) {
    this.setState({
      business_gst_number: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    console.log(`The values are ${this.state.person_name}, ${this.state.business_name}, and ${this.state.business_gst_number}`)
    this.setState({
      person_name: '',
      business_name: '',
      business_gst_number: ''
    })
  }



    render()
    {
        return(
            <Layout>
                 <form onSubmit={this.onSubmit}>
                <label>
                  Name:
                 <input type="text"  value={this.state.person_name} onChange={this.onChangePersonName} />
                </label>
                <label>
                Description:
                 <input type="text" description={this.state.description}  />
                </label>
                {/* <label>
                Iscomplete:
                 <input type="checkbox"  checked={this.state.Iscomplete}
                onChange={this.handleInputChange}  />
                </label> */}
                <input type="submit" value="Submit" />
                </form>
            <div>
               <Link href='/'>
                    <a>Return to dashboard</a>
                </Link>
            </div>
            </Layout>
        );
    }
}

export default AddRecord;
