import React ,{ Component } from "react";
import Link from "next/link";
import fetch from 'isomorphic-fetch';
import Layout from "../Components/Layout";
import { Table, Col, Row } from 'react-bootstrap';


class AddRecord extends Component{

    constructor(props) {
        super(props);    

      this.onChangeName = this.onChangeName.bind(this);
      this.onChangeDescription = this.onChangeDescription.bind(this);
      this.onChangeIscomplete = this.onChangeIscomplete.bind(this);
      this.onSubmit = this.onSubmit.bind(this);

      this.state = {
          Name: '',
          description: '',
          Iscomplete:''
      }
  }
  onChangeName(e) {
    this.setState({
      Name: e.target.value
    });
  }
  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })  
  }
  onChangeIscomplete(e) {
    this.setState({
      Iscomplete: e.target.value
    })
  }

  onSubmit(e) {
    e.preventDefault();
    console.log(`The values are ${this.state.Name}, ${this.state.description}, and ${this.state.Iscomplete}`)
    this.setState({
      Name: '',
      description: '',
      Iscomplete: ''
    })
    fetch('http://localhost:50607/api/item', {
      method: 'POST',
      body: this.setState,
    });
  }



    render()
    {
        return(
            <Layout>
                 <form onSubmit={this.onSubmit}>
                <label>
                  Name:
                 <input type="text"  value={this.state.Name} onChange={this.onChangeName} />
                </label>
                <label>
                Description:
                 <input type="text" value={this.state.description} onChange={this.onChangeDescription}  />
                </label>
                <label>
                Iscomplete:
                 <input type="checkbox"  checked={this.state.Iscomplete}
                onChange={this.onChangeIscomplete}  />
                </label>
                <input type="submit" value="Submit" />
                </form>
            <div>
               <Link href='/'>
                    <a>Return to dashboard</a>
                </Link>
            </div>
            </Layout>
        );
    }
}

export default AddRecord;
